SMSSDK.getInstance().getSmsCodeAsyn("159xxxxxxxx", "1", new SmscodeListener() {
    @Override
    public void getCodeSuccess(final String uuid) {
        // 获取验证码成功，uuid 为此次获取的唯一标识码。
    }

    @Override
    public void getCodeFail(int errCode,final String errmsg) {
        // 获取验证码失败 errCode 为错误码，详情请见文档后面的错误码表; errmsg 为错误描述。
    }
});

SMSSDK.getInstance().checkSmsCodeAsyn("159xxxxxxxx", "123456", new SmscheckListener() {
    @Override
    public void checkCodeSuccess(final String code) {
        // 验证码验证成功，code 为验证码信息。
    }

    @Override
    public void checkCodeFail(int errCode, final String errmsg) {
        // 验证码验证失败, errCode 为错误码，详情请见文档后面的错误码表；errmsg 为错误描述。
    }
});

接口说明  注册SDK接口：在官网创建应用,创建成功后自动生成 AppKey 用以标识该应用  调用示例：  - (BOOL)application:(UIApplication *)application didFinishLaunchingWithOptions:(NSDictionary *)launchOptions {
      // Override point for customization after application launch.

      //从官网注册获取
      //注册短信验证的appKey
      [JSMSSDK registerWithAppKey:kAppKey];

      return YES;
 }  + (void)getVerificationCodeWithPhoneNumber:(NSString * __nullable)number 
                          andTemplateID:(NSString * __nullable)templateID
                          completionHandler:(JSMSCompletionHandler __nullable)handler;  接口说明  获取验证码接口：创建获取验证码的按钮，或者是在自己已有的界面的按钮事件里调用获取验证码的方法  参数说明：   number :手机号码  tempID：短信模板ID，目前服务器只提供+ tempID：@“1”  handler：请求成功或者失败的回调   调用示例：  
//点击获取验证码
 - (IBAction)sendAuthCode:(id)sender {

     [self.view endEditing: YES];
     [JSMSSDK getVerificationCodeWithPhoneNumber:@ 15220000000  andTemplateID:@ 1  completionHandler:^(id resultObject, NSError *error) {
     if (!error) {
         NSLog(@ Get Verification Code success! );
     }else{
         NSLog(@ Get Verification Code failure! );
     }];
 }  + (void)commitWithPhoneNumber:(NSString * __nullable)number
                     verificationCode:(NSString * __nullable)vCode
                  completionHandler:(JSMSCompletionHandler __nullable)handler;  接口说明  验证验证码：创建提交验证码的按钮，或者是在自己已有的界面的按钮事件里调用提交验证码的方法  参数说明：   number  手机号码  vCode:短信验证码  handler:请求成功或者失败的回调   调用示例：  //验证验证码 
 [JSMSSDK commitWithPhoneNumber:@ 15220000000  verificationCode:@ 123456  completionHandler:^(id resultObject, NSError *error) {
      if (!error) {
           NSLog(@ Commit Verification Code success! );
      }else{
           NSLog(@ %@ ,error);
           NSLog(@ Commit Verification Code failure! );
      }
 }];

